sys_script.script:
(function executeRule(current, previous /*null when async*/) {
  // Add your code here
  var possessionGA = new GlideAggregate("x_965246_library_c_possession");
  possessionGA.addAggregate("COUNT", "subscriber");
  possessionGA.query("subscriber", gs.getUserID());
  possessionGA.query();
  var possessionUserCount = 0;

  if (possessionGA.next()) {
    possessionUserCount = possessionGA.getAggregate("COUNT", "subscriber") * 1;
  }

  var feedbackGA = new GlideAggregate("x_965246_library_c_feedback");
  feedbackGA.addAggregate("COUNT");
  feedbackGA.query("subscriber", gs.getUserID());
  feedbackGA.query();
  var feedbackUserCount = 0;
  if (feedbackGA.next()) {
    feedbackUserCount = feedbackGA.getAggregate("COUNT") * 1;
  }

  if (possessionUserCount <= feedbackUserCount) {
    gs.addInfoMessage(
      "Warning message: you have " +
        possessionUserCount +
        " possessions. Number of feedbacks: " +
        feedbackUserCount +
        "."
    );
  }
})(current, previous);
